//import ShowCurrentPosts from "./ShowCurrentPosts";

class CreateAPost extends Component {



    // console.log(text);


  handleSubmit = event => {
    event.preventDefault();
    let post = {
      title: this.state.title,
      text: this.state.text,
      content: this.state.content
    };
    console.log(post);
    this.setState({
      showResult: [...this.state.showResult, post]
    });
  };

  render() {
    return (
      <section>
        <form onSubmit={this.handleSubmit}>
          <label>
            UserName:
            <input
              type="text"
              ref="txt"
              /*  text={this.state.text} */

            />
          </label>
          <br />

          <br />
          <label>
            Title:
            <input
              type="text"
              ref="tit"
              /* title={this.state.title} */

            />
          </label>
          <br />

          <br />

          <label className="box">
            Content:
            <textarea
              type="text"
              ref="con"
              /* content={this.state.content} */

            />
          </label>
          <br />

          <button type="submit" /*  onClick={this.submitValue} */>
            Creat Post
          </button>
        </form>
      </section>
    );
  }
}

export default CreateAPost;
