import React, { Component } from "react";
//import ShowCurrentPosts from "./ShowCurrentPosts";

class CreateAPost extends Component {
  state = {
    text: "",
    title: "",
    content: "",
    showResult: []
  };

  handleChange = event => {
    this.setState({
      text: this.refs.txt.value,
      title: this.refs.tit.value,
      content: this.refs.con.value
    });

    // console.log(text);
  };

  handleSubmit = event => {
    event.preventDefault();
    let post = {
      title: this.state.title,
      text: this.state.text,
      content: this.state.content
    };
    console.log(post);
    this.setState(newState => ({
      showResult: [...newState]
    }));
  };

  render() {
    return (
      <section>
        <form onSubmit={this.handleSubmit}>
          <label>
            UserName:
            <input
              type="text"
              ref="txt"
              /*  text={this.state.text} */
              onChange={this.handleChange}
            />
          </label>
          <br />

          <br />
          <label>
            Title:
            <input
              type="text"
              ref="tit"
              /* title={this.state.title} */
              onChange={this.handleChange}
            />
          </label>
          <br />

          <br />

          <label className="box">
            Content:
            <textarea
              type="text"
              ref="con"
              /* content={this.state.content} */
              onChange={this.handleChange}
            />
          </label>
          <br />

          <button type="submit" /*  onClick={this.submitValue} */>
            Creat Post
          </button>
        </form>
      </section>
    );
  }
}

export default CreateAPost;
