    /*   const result = this.props.showResult.map((list,index)=>(
          <li key={index}>{list}</li> 

       )) ;  */
        console.log(this.props.arr);

    return (
      <div className="result">

        {this.props.arr.map ((item,index)=>{
          return (
            < div key={index}>
            <p> {item.title}</p><hr />
            <p>{item.text}</p><hr />
            <p>{item.content}</p><hr />
            <p>{this.props.calender}</p>
</div>          
          )
        })}

      </div>
    );
  }
}

export default ShowCurrentPosts;
