import React, { Component } from "react";
//import ShowCurrentPosts from "./ShowCurrentPosts";

class CreateAPost extends Component {
  constructor(props) {
    super(props);

    this.state = {
      text: "",
      title: "",
      content: "",
      showResult: []
    };
  }

  handleChange = event => {
    const inputTxt = event.target.value;
    this.setState({
      text: inputTxt,
      title: event.target.value,
      content: event.target.value
    });
    //console.log(inputTxt, 'input');
  };

  handleSubmit = event => {
    event.preventDefault();
    //let formValue = document.getElementsByTagName("input");
    //console.log(formValue[].value);
    console.log(this.state);

    /* this.setState(newState => ({
      showResult: [...newState.showResult, this.state.text]
    })); */
  };

  /* submitValue = e => {
    console.log(this.state.result);
  }; */

  render() {
    return (
      <section>
        <form onSubmit={this.handleSubmit}>
          <label>
            UserName:
            <input
              type="text"
              text={this.state.text}
              onChange={this.handleChange}
            />
          </label>
          <br />

          <br />
          <label>
            Title:
            <input
              type="text"
              title={this.state.title}
              onChange={this.handleChange}
            />
          </label>
          <br />

          <br />

          <label className="box">
            Content:
            <textarea
              type="text"
              content={this.state.content}
              onChange={this.handleClick}
            />
          </label>
          <br />

          <button type="submit" onClick={this.submitValue}>
            Creat Post
          </button>
        </form>
      </section>
    );
  }
}

export default CreateAPost;
