import React from 'react';
import './App.css';
import {BrowserRouter as Router, Link, Route, Switch} from 'react-router-dom';
import Home from './components/Home';
import CreateAPost from './components/CreateAPost';
import ShowCurrentPosts from './components/ShowCurrentPosts';


export default class App extends React.Component {
  state={
    text: "",
    title: "",
    content: "",
    showResult: [],
    time: new Date()
  }

 /*  handleChange =(a,b,c) => {
    this.setState({
      text: a,
      title: b, 
      content: c
    });
  } */

  handleSubmit =(a,b,c,d) => {

    let time = new Date()
    //event.preventDefault();
    let post = {
      title: a,
      text: b,
      content: c,
      time:d
    };
    console.log(post);
    console.log(time);

    this.setState({
      showResult: [...this.state.showResult, post]
    });
  };
  render(){
  return (
    <Router>
    <section>
      <nav className="navigation">
        <li><Link to="/" className="link">Home</Link></li>
        <li><Link to ="CreateAPost" className="link">Create A Post</Link></li>
        <li><Link to="ShowCurrentPosts" className="link">Show Current Posts</Link></li>
      </nav>
      <Switch>
        <Route exact path="/" component={Home} /* text={this.state.text} */ />
        <Route path="/CreateAPost" render={()=> <CreateAPost handleSubmit={this.handleSubmit} />} />
        <Route path="/ShowCurrentPosts" render= {()=> <ShowCurrentPosts arr={this.state.showResult} />} />

        </Switch>
      <p>{this.state.time}</p>
    </section>

    </Router>
  )
}
}
