import React, { Component } from "react";
//import ShowCurrentPosts from "./ShowCurrentPosts";

class CreateAPost extends Component {
  constructor(props) {
    super(props);

    this.textInput = React.createRef();
    this.title = React.createRef();
    this.content = React.createRef();

    this.state = {
      /* text: "",
      title: "",
      content: "",
      showResult: [] */
    };
  }

  handleChange = event => {
    this.setState({
      /* text: this.refs.txt.value,
      title: this.refs.tit.value,
      content: this.refs.con.value */
    });
    // console.log(text);
  };

  handleSubmit = event => {
    event.preventDefault();
    let formValue = document.getElementsByTagName("input");
    console.log(formValue.value);
    console.log(this.state);

    //   this.setState(newState => ({
    //     showResult: [...newState.showResult]
    //   }));
    //   console.log(this.showResult);
  };

  render() {
    return (
      <section>
        <form onSubmit={this.handleSubmit}>
          <label>
            UserName:
            <input
              type="text"
              ref="this.textInput"
              /* text={this.state.text} */
              onChange={this.handleChange}
            />
          </label>
          <br />

          <br />
          <label>
            Title:
            <input
              type="text"
              ref="this.title"
              /* title={this.state.title} */
              onChange={this.handleChange}
            />
          </label>
          <br />

          <br />

          <label className="box">
            Content:
            <textarea
              type="text"
              ref="this.content"
              /* content={this.state.content} */
              onChange={this.handleChange}
            />
          </label>
          <br />

          <button type="submit" /*  onClick={this.submitValue} */>
            Creat Post
          </button>
        </form>
      </section>
    );
  }
}

export default CreateAPost;
